      langs: ['zh', 'en'],
      // 设置控制显示
      settingVisible: false,
      // 搜索时延
      timeout: null,
      params: {
        keyword: '',
        illustType: 'illust'
      },
      keywords: [],
      squareUrl:
        'https://cube.elemecdn.com/9/c2/f0ee8a3c7c9638a54940382568c9dpng.png',
      // 热门搜索模块
      hotTags: []
    };
  },
  computed: {
    // 辅助函数取出x内用户信息
    ...mapGetters(['user']),
    MenuList() {
      return [
        {
          name: this.$t('followed'),
          handler: 'followed'
        },
        {
          name: this.$t('bookmarked'),
          handler: 'bookmarked'
        },
        // {
        //   name: '画集',
        //   handler: 'mycollect'
        // },
        {
          name: this.$t('spotLight'),
          handler: 'spotLight'
        },
        {
          name: this.$t('setting'),
          handler: 'setting'
        },
        {
          name: this.$t('logout'),
          handler: 'logout',
          divided: true
        }
      ];
    },
    typeList() {
      return [
        {
          name: this.$t('illust'),
          value: 'illust'
        },
        {
          name: this.$t('manga'),
          value: 'manga'
        },
        {
          name: this.$t('artist'),
          value: 'artist'
        }
      ];
    }
  },
  watch: {
    // 'params.keyword': {
    //   handler: 'getKeywords'
    // }
  },
  mounted() {
    this.params.illustType = this.$route.query.illustType || 'illust';
    this.params.keyword = this.$route.query.tag;
    this.getHotTag();
  },
  methods: {
    changeLocaleLang(val) {
      this.$i18n.locale = val;
      cookie.set('lang', val, {
        expires: 365
      });
    },
    // 点击 用户模块
    clickMenu(type) {
      switch (type) {
        case 'followed':
          this.toFollowed();
          break;
        case 'bookmarked':
          this.toBookmarked();
          break;
        case 'setting':
          this.setModal();
          break;
        case 'mycollect':
          this.toMycollect();
          break;
        case 'logout':
          this.logout();
          break;
        case 'spotLight':
          this.toSpotLight();
          break;
        default:
          break;
      }
    },
    // 获取标签数据
    getHotTag() {
      this.$api.search.getHotTag().then(res => {
        this.hotTags = res.data.data.splice(0, 9);
      });
    },
    // 跳转关注页
    toFollowed() {
      this.$router.push({
        path: '/users/followed',
        query: {
          userId: this.user.id
        }
      });
    },
    toMycollect() {
      this.$router.push({
        path: '/collect/mycollection',
        query: {
          userId: this.user.id
        }
      });
    },
    // 跳转书签页
    toBookmarked() {
      this.$router.push({
        path: '/users/bookmarked'
      });
    },
    toSpotLight() {
      this.$router.push({
        path: '/spot-light/index'
      });
    },
    // 设置弹窗
    setModal() {
      this.settingVisible = !this.settingVisible;
    },
    // 退出登录
    logout() {
      this.$confirm(this.$t('user.logoutMessage'))
        .then(_ => {
          this.$message.info(this.$t('user.logoutSuc'));
          cookie.remove('jwt');
          this.$store.dispatch('clearCurrentState');
          window.location.href = '/';
        })
        .catch(_ => {});
    },
    // 获取关键词
    getKeywords() {
      this.$api.search
        .getKeyword(this.params.keyword)
        .then(({ data: { data }}) => {
          if (data && data.keywordList) {
            this.keywords = data.keywordList || [];
          }
        });
    },
    // 搜索相关信息
    querySearch(queryString, cb) {
      const result = this.keywords.map(e => {
        return { value: e };
      });
      clearTimeout(this.timeout);
      this.timeout = setTimeout(() => {
        cb(result);
      }, 1000);
    },
    // 选择
    handleSelect(e) {
      console.log('##########');

      this.handleSearch();
    },
    // 搜索跳转
    handleSearch() {
      const keyword = this.params.keyword;
      if (!keyword.trim()) {
        return;
      }
      this.$router.push({
        path: `/keywords`,
        query: {
          tag: keyword,
          illustType: this.params.illustType
        }
      });
    },
    // 点击tag
    handleClickTag(d) {
      this.$router.push({
        path: `/keywords`,
        query: {
          tag: d.name,
          illustType: this.params.illustType
        }
      });
    },
    // 打卡用户系统
    signUp() {
      if (!cookie.get('jwt')) {
        this.$store.dispatch('setLoginBoolean', false);
      }
    },
    login() {
      if (!cookie.get('jwt')) {
        this.$store.dispatch('setLoginBoolean');
      }
    }
  }
};
</script>

