<script>
import { mapGetters } from 'vuex';
export default {
  name: 'CollectPicture',
  components: {},
  data() {
    return {
      page: { page: 1, pageSize: 20, total: 0 },
      collectionList: [],
      isPublic: 0,
      collectionId: ''
    };
  },
  computed: {
    ...mapGetters(['readyCollectItem', 'user'])
  },
  watch: {
    '$store.getters.collectBoolean': function(val) {
      if (val) {
        this.collectionId = '';
        this.getCollections();
      }
    }
  },
  mounted() {},
  methods: {
    selectCollect(collect) {
      this.collectionId = collect.id;
    },
    typeChange() {
      this.loginType = !this.loginType;
    },
    getCollections() {
      if (
        this.collectionList.length < this.page.page * this.page.pageSize &&
        this.page.page !== 1
      ) {
        return;
      }
      this.$api.collect
        .getUserCollections({
          page: this.page.page++,
          pageSize: this.page.pageSize,
          userId: this.user.id
        })
        .then(res => {
          const {
            data: { data }
          } = res;
          if (data && data.length) {
            this.collectionList = data;
          } else {
            this.$message.info('画集列表已到底');
          }
        });
    },
    closeModal() {
      this.$store.dispatch('setCollectBoolean');
    },
    collectAjax() {
      this.$api.collect
        .postCollectionsIllust({
          illust_id: this.readyCollectItem.id,
          collectionId: this.collectionId
        })
        .then(res => {
          if (res.data.data) {
            this.closeModal();
            this.$message.success('加入画集成功');
          } else {
            this.$message.error('加入画集失败');
          }
        });
    }
  }
};
</script>

